// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.8.0;

contract Products {
    uint public count = 0;
    struct Product{
        string p_name;
        address p_manufacturer;
        uint p_qty;
        uint p_price;
    }
    mapping (uint => Product) public P_list;

    function AddProduct (string memory _name, uint qty, uint price) public {
        count++;
        P_list[count]=Product(_name, msg.sender, qty, price);
    }

    function BuyProduct (uint _id, uint _qty) public {    
       
        uint _q=P_list[_id].p_qty-_qty;
        P_list[_id]=Product(P_list[_id].p_name,P_list[_id].p_manufacturer, _q,P_list[_id].p_price);
    }

    
  
}
